mapper.select:
select tr.id, u1.name as owner, tr.money, tr.name, tr.expected_date, cu.name as customer_id, dv1.value as stage, dv2.value as type, dv3.value as source, ac.name as activity_id,co.fullname as contacts_id, u2.name as create_by, tr.create_time, u3.name as edit_by, tr.edit_time, tr.description, tr.contact_summary, tr.next_contact_time from tbl_tran tr join tbl_user u1 on tr.owner = u1.id join tbl_customer cu on tr.customer_id = cu.id join tbl_dic_value dv1 on tr.stage = dv1.id left join tbl_dic_value dv2 on tr.type = dv2.id left join tbl_dic_value dv3 on tr.source = dv3.id left join tbl_activity ac on tr.activity_id = ac.id left join tbl_contacts co on tr.contacts_id = co.id join tbl_user u2 on tr.create_by = u2.id left join tbl_user u3 on tr.edit_by = u3.id
and u1.name like '%' #{owner} '%'
and tr.name like '%' #{name} '%'
and cu.name like '%' #{customerName} '%'
and tr.stage = #{stage}
and tr.type = #{type}
and tr.source = #{source}
and co.fullname like '%' #{contactsName} '%'
limit #{beginNo},#{pageSize}

select count(1) from tbl_tran tr join tbl_user u1 on tr.owner = u1.id join tbl_customer cu on tr.customer_id = cu.id join tbl_dic_value dv1 on tr.stage = dv1.id left join tbl_dic_value dv2 on tr.type = dv2.id left join tbl_dic_value dv3 on tr.source = dv3.id left join tbl_activity ac on tr.activity_id = ac.id left join tbl_contacts co on tr.contacts_id = co.id join tbl_user u2 on tr.create_by = u2.id left join tbl_user u3 on tr.edit_by = u3.id
and u1.name like '%' #{owner} '%'
and tr.name like '%' #{name} '%'
and cu.name like '%' #{customerName} '%'
and tr.stage = #{stage}
and tr.type = #{type}
and tr.source = #{source}
and co.fullname like '%' #{contactsName} '%'
